import os, imghdrfrom flask import Flask, render_template, requestfrom mnist_cnn.predict import predictapp = Flask(__name__)APP_ROOT = os.path.dirname(os.path.abspath(__file__))@app.route('/')def index():    return render_template('upload.html')@app.route('/', methods=['POST'])def upload():    target = os.path.join(APP_ROOT, 'static')    image1 = request.files.getlist('first_digit')[0]    image2 = request.files.getlist('second_digit')[0]    filename1 = image1.filename    filename2 = image2.filename    destination1 = os.path.join(target, filename1)    destination2 = os.path.join(target, filename2)    image1.save(destination1)    image2.save(destination2)    result1 = predict(destination1, 'linear')    print('result:', result1)    result2 = predict(destination2, 'linear')    print('result2:', result2)    return render_template('upload.html',             first_image=filename1,            second_image=filename2,            first_predict=result1,            second_predict=result2            )if __name__ == '__main__':    app.run(debug=True)